    // to use the other TryCreate().
    [Pure]
    static bool IMonth<EthiopicMonth>.TryCreate(int year, int month, out EthiopicMonth result)
    {
        var monthValue = TryCreate(year, month);
        result = monthValue ?? default;
        return monthValue.HasValue;
    }

