    {
        var sch = Calendar.Schema;
        int startOfYear = sch.CountDaysSinceEpoch(Year, 1);
        int daysInYear = sch.CountDaysInYear(Year);
        return Range.StartingAt(InternationalFixedDate.UnsafeCreate(startOfYear), daysInYear);
    }

